import React, { useEffect } from 'react'
import io from 'socket.io-client'
import {useLocation} from 're'

const socket = io.connect("http://http://localhost:5000")

const Chat = () => {

  const location = useLocation()

  useEffect(() => {
    socket.emit('join')
  },[])

  return (
    <div>Chat</div>
  )
}

export default Chat
